namespace Crawlers;

public static class FileUtils
{
    public static string GetTempFilename() => Path.Combine(Path.GetTempPath(), $"temp_file_{new Random().Next()}.tmp");
    public static string GetTempFilename(string ext) => Path.Combine(Path.GetTempPath(), $"temp_file_{new Random().Next()}.{ext}");

    /// <summary>
    /// Retrieves the MIME type associated with a registered file based on its file extension.
    /// </summary>
    /// <param name="extension">The file extension to look up, including the leading period (e.g., ".txt").</param>
    /// <returns>The MIME type as a string if the extension is found; otherwise, an empty</returns>
    public static string FileTypeToMimeType(string extension)
    {
        return ExtensionToRegisteredFile.TryGetValue(extension, out var rf) ? rf.MimeType : "";
    }

    /// <summary>
    /// return the maximum allowed size for a given mime-type
    /// </summary>
    /// <param name="mimeType">the mime-type to return a size for</param>
    /// <returns>the maximum size for this mime-type in bytes</returns>
    public static long MaximumSizeInBytesForMimeType(string mimeType)
    {
        if (_mimeTypeToRegisteredFile.TryGetValue(mimeType, out var rf))
        {
            return rf.MaxFileSize; // already in bytes
        }
        return 10L * 1024L * 1024L; // Default max size
    }

    
    /// <summary>
    /// return true if this is a known mime-type and is supported
    /// </summary>
    /// <param name="mimeType">the mime-type to check</param>
    /// <returns>true if this mime-type is known, and is supported by SimSage</returns>
    public static bool IsValidMimeType(string mimeType)
    {
        return _mimeTypeToRegisteredFile.TryGetValue(mimeType, out var rf) && rf.Supported;
    }
    
    public static void Shutdown(int exitCode) { Environment.Exit(exitCode); }

    private static readonly Dictionary<string, RegisteredFile> ExtensionToRegisteredFile = new Dictionary<string, RegisteredFile>();
    private static Dictionary<string, RegisteredFile> _mimeTypeToRegisteredFile = new Dictionary<string, RegisteredFile>();
    private static readonly object UtLock = new object();

    private static void AddMimeType(string mimeType, RegisteredFile rf)
    {
        if (_mimeTypeToRegisteredFile.TryGetValue(mimeType, out var existing))
        {
            lock (UtLock)
            {
                existing.ExtensionList.AddRange(rf.ExtensionList);
            }
        }
        else
        {
            _mimeTypeToRegisteredFile[mimeType] = rf;
        }
    }
    
